import React, { Component } from 'react';
import logo from './gremlin-neptune.png';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Paper from '@material-ui/core/Paper';
import ReactJson from 'react-json-view';
import './App.css';

var graphOutput =
  [
    {
      'name': 'Star Wars: Return of the Jedi',
      'releaseDate': '1983',
      'director': 'George Lucas'
    }
  ];




class App extends Component {

  constructor(props) {
    super(props);
    this.state = { value: [
        {
          'name': 'Star Wars: Return of the Jedi',
          'releaseDate': '1983',
          'director': 'George Lucas'
        }
      ]
    };

    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Welcome to AWS re:Invent 2018 Workshop: <br/>
            DAT310 - Building a New Graph Application Using Amazon Neptune.
          </p>
        </header>
        <p>
          <form onsubmit={this.handleSubmit}>
            <TextField
              id="standard-full-width"
              label="Enter Name of a Movie:"
              style={{ margin: 8, width: 600 }}
              placeholder="<Insert Movie Title Here>"
              helperText="Ex: Casablanca"
              margin="normal"
              InputLabelProps={{
                shrink: true,
              }}
            /><br/>
            <Button variant="contained" size="medium" color="primary">
              Go!
            </Button>
          </form><br/><br/>
        </p>
        <Paper className="graph-output">
            <h3 align="left">Graph Traversal:</h3>
            <p align="left">
              g.V().hasLabel('movie').has('name','Star Wars: Return of the Jedi').valueMap()
            </p>
        </Paper>
        <Paper className="graph-output">
            <h3 align="left">Graph Traversal Output:</h3>
            <p align="left">
              <ReactJson src={this.state.value} />
            </p>
        </Paper>
      </div>
    );
  }
}

export default App;
